import React, {useState, useEffect} from 'react'
import {useNavigate} from 'react-router-dom'

const Filtering = () => {

    let navigate = useNavigate()
    let [filterLink, setFilterLink] = useState({
        free: false,
    })


    let freeID = document.querySelector('#free')
    let masterID = document.querySelector('#master')


    let filterNavigate = (dictOfFilters) => {
        console.log(dictOfFilters)
        let link = 'f'
        for (const [key, value] of Object.entries(dictOfFilters)) {
            if(value){
                link = link + `;${key};`
            }


            console.log(link);
          }

        // navigate(`/${link}`)
    }

    let addFilter = (e) => {
        // console.log(filterLink)
        
        const {value} = e.target 
        // e.preventDefault()
        if(e.target.checked){
            console.log('if OK')
            setFilterLink(filterLink => ({...filterLink, [value]: false}))
        }else{
            console.log('if NOT OK')
            setFilterLink(filterLink => ({...filterLink, [value]: true}))
        }
        console.log(typeof(e.target.checked))
        console.log(e.target.checked)
        console.log(filterLink)

        filterNavigate(filterLink)
    }


    let funff = () => {
        let freeID = document.querySelector('#free')
        let masterID = document.querySelector('#master')

        // isFilterChecked(setFilterLink, filterLink, freeID)
        // isFilterChecked(setFilterLink, filterLink, masterID)
        if(freeID.checked){
            setFilterLink(filterLink => ({...filterLink, free: true}))          
        }else{
            setFilterLink(filterLink => ({...filterLink, free: false}))
        }
        console.log(filterLink)

        // if(masterID.checked){
        //     setFilterLink(filterLink => ({...filterLink, [masterID.value]: true}))          
        // }else{
        //     setFilterLink(filterLink => ({...filterLink, [masterID.value]: false}))
        // }


        // if(masterID.checked){
        //     setFilterLink(filterLink => ({...filterLink, [masterID.value]: false}))
        // }else{
        //     setFilterLink(filterLink => ({...filterLink, [masterID.value]: true}))
        // }


    }
    
    useEffect(() => {
        funff()
    }, [])

    
  return (
    <div >
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="free" value="free" onChange={addFilter} defaultChecked={true} />
            <label className="form-check-label" htmlFor="inlineCheckbox1">Free</label>
        </div>
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="master" value="master" onChange={addFilter} defaultChecked={false}/>
            <label className="form-check-label" htmlFor="inlineCheckbox2">Master</label>
        </div>
        
    </div>
  )
}

export default Filtering
