import React, { useState, useEffect } from 'react'
import { useLocation, useParams  } from 'react-router-dom'


const Paginator = () => {
    let location = useLocation()
    let page = useParams()
    let [totalPageNumber, setTotalPageNumber] = useState([])
    let [pagesList, setPagesList] = useState([])

    let getTotalPageNumber = async () => {
        let reponse = await fetch('http://127.0.0.1:8000/api/courses/page/total-page-number')
        let data = await reponse.json()
        setTotalPageNumber(data.total_page_number)
    }

    let getPagesList = () => {
        if(location.pathname === '/'){
            setPagesList([1, 2, 3])        

        }else{
            console.log(page)
            // setPagesList([page - 1, page, page + 1])


        }



    }


    useEffect(() => {
        getTotalPageNumber()
        getPagesList()     
    }, [])

  return (
    <div style={{paddingTop: '3rem'}}>
        <nav aria-label="Page navigation example">
            <ul className="pagination justify-content-center">
                <li className="page-item disabled">
                <a className="page-link">Previous</a>
                </li>
                <li className="page-item"><a className="page-link" href="">{pagesList[0]}</a></li>
                <li className="page-item"><a className="page-link" href="#">{pagesList[1]}</a></li>
                <li className="page-item"><a className="page-link" href="#">{pagesList[2]}</a></li>
                <li className="page-item">
                <a className="page-link" href="#">Next</a>
                </li>
            </ul>
        </nav>




    </div>
  )
}

export default Paginator
