import React from 'react'

import {
    useParams, 
    useNavigate,
    Link,
       
} from 'react-router-dom'

const EditProfileEmail = () => {
    const navigate = useNavigate()

    let username = useParams().username

    // alerts
    function alert(text, id){
        let passwordDiv = document.querySelector(`#${id}`)
        let msg = document.createElement('p')                
        msg.innerText = text
        msg.style.color = 'red'
        passwordDiv.appendChild(msg)
        setTimeout(() => {
            passwordDiv.removeChild(msg);
          }, 4000)
        navigate('/')
    }

    let getUserData = async (e)  => {
        e.preventDefault()

        let response = await fetch(`http://127.0.0.1:8000/member/api/edit/${username}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                'operation': 'email-edit',
                'password': e.target.password.value,
                'email': e.target.email.value,
            })
        })
        
        if(response.status === 200){
            // alert('Your email has been changed', 'btn')
            console.log(response.status)
            console.log(response)
        }else{
            console.log('smth wrong')
            console.log(response.status)
            console.log(response)


        }
        
    }

  return (
    <div>
        <p>change your email {username}</p>
        <form onSubmit={getUserData}>
            <input type='password' name='password' placeholder='password'/>
            <input type='email' name='email' placeholder='email'/>
            <button id='btn' type='submit'>Edit email</button>
            
        </form>
        
    </div>
  )
}

export default EditProfileEmail
