import React, {useState, useEffect} from 'react'
import { CoursesList } from '../components/CoursesList'
import Filtering from '../components/Filtering'
import Paginator from '../components/Paginator'

const HomePage = () => {
  let [filter, setFilter] = useState(false)
  let [page, setPage] = useState(1)
    
  let freeFilterHandler = () => {        
    setFilter(!filter)  
    // sessionStorage.setItem('freeFilter', freeFilter)    
}

  let getPage = () => {
    setPage(page => [...page, page + 1])
  }

useEffect(() => {
  console.log(filter)
},[filter])

  return (
    <div className='container' style={{paddingTop:'3rem'}}>
        <div className="form-check form-check-inline">
              <input className="form-check-input" type="checkbox" id="free" value="free" onChange={freeFilterHandler} checked={filter}  />
              <label className="form-check-label" htmlFor="inlineCheckbox1">Free</label>
          </div>

        {/* <Filtering /> */}
        <CoursesList filter={filter} />
        <Paginator />
        <button onClick={getPage}>next page</button>
    </div>
  )
}

export default HomePage
