import React, { useState, useEffect } from 'react'
import { useLocation, useParams, Link  } from 'react-router-dom'


const Paginator = () => {
    let location = useLocation()
    let currentPage = useParams()
    let [totalPageNumber, setTotalPageNumber] = useState([])
    let [pagesList, setPagesList] = useState([])
    let [firstLoad, setFirstLoad] = useState(true)

    let getTotalPageNumber = async () => {
        let reponse = await fetch('http://127.0.0.1:8000/api/courses/page/total-page-number')
        let data = await reponse.json()
        setTotalPageNumber(data.total_page_number)
    }

    function reload(){
        if(firstLoad){
            window.location.reload(false)
            setFirstLoad(false)
        }
            
    }

    let getPagesList = () => {
        if(location.pathname === '/'){
            setPagesList([1, 2, 3])        

        }else{
            let page = parseFloat(currentPage.page)
            setPagesList([page - 1, page, page + 1, ])


        }



    }


    useEffect(() => {
        // reload()

        getTotalPageNumber()
        getPagesList()   
        console.log(pagesList)  
    }, [])

  return (
    <div style={{paddingTop: '3rem'}}>
        <nav aria-label="Page navigation example">
            <ul className="pagination justify-content-center">
                <li className="page-item disabled">
                <a className="page-link">Previous</a>
                </li>
                <li className="page-item"><Link className="page-link" to={`/page/${pagesList[0]}`}>{pagesList[0]}</Link></li>
                <li className="page-item"><Link className="page-link" to={`/page/${pagesList[1]}`}>{pagesList[1]}</Link></li>
                <li className="page-item"><Link className="page-link" to={`/page/${pagesList[2]}`}>{pagesList[2]}</Link></li>
                <li className="page-item">
                <a className="page-link" href="#">Next</a>
                </li>
            </ul>
        </nav>




    </div>
  )
}

export default Paginator
