import React, {useState, useEffect} from 'react'
import {useNavigate} from 'react-router-dom'

const Filtering = () => {

    let navigate = useNavigate()
    let [filterLink, setFilterLink] = useState({})


    let freeID = document.querySelector('#free')
    let masterID = document.querySelector('#master')


    let filterNavigate = () => {
        navigate(`/${filterLink}`)
    }
    console.log(filterLink)


    let addFilter = (e) => {
        const {value} = e.target
        if(e.target.checked){
            setFilterLink(filterLink => ({...filterLink, [value]: 'true'}))
        }else{
            setFilterLink(filterLink => ({...filterLink, [value]: 'false'}))
        }       
    }
    let addFilter2 = (e) => {
        if(e.target.checked){
            setFilterLink(filterLink => ({...filterLink, master: 'true'}))
        }else{
            setFilterLink(filterLink => ({...filterLink, master: 'false'}))
        }       
    }
    

    
  return (
    <div >
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="free" value="free" onChange={addFilter}/>
            <label className="form-check-label" htmlFor="inlineCheckbox1">Free</label>
        </div>
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="master" value="master" onChange={addFilter2} />
            <label className="form-check-label" htmlFor="inlineCheckbox2">Master</label>
        </div>
        
    </div>
  )
}

export default Filtering
