import React from 'react'
// import { Link } from 'react-router-dom'


const CourseListItem = (props) => {

    let getBody = (props) => {
        return props.body.slice(0, 30)
    }

  return (


    <div>
        <h2>{props.name}</h2>
        <p>{getBody(props)}</p>

    </div>
  )
}

export default CourseListItem
