import React, {useState, useEffect} from 'react'
import {useNavigate} from 'react-router-dom'

const Filtering = () => {

    let navigate = useNavigate()
    let [filterLink, setFilterLink] = useState('')

    let filterNavigate = () => {
        navigate(`/${filterLink}`)
    }
    console.log(filterLink)


    let addFilter = (e) => {
        if(!e.target.checked){
            setFilterLink(filterLink => ({...filterLink, filter: e.target.value}))
        }
            setFilterLink(filterLink => [...filterLink, e.target.value])

        console.log(filterLink)   

    }

    

  return (
    <div >
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="inlineCheckbox1" value="free" onChange={addFilter}/>
            <label className="form-check-label" htmlFor="inlineCheckbox1">Free</label>
        </div>
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="inlineCheckbox2" value="master" onChange={addFilter}/>
            <label className="form-check-label" htmlFor="inlineCheckbox2">Master</label>
        </div>
        
    </div>
  )
}

export default Filtering
