import React from 'react'
// import { Link } from 'react-router-dom'


const CourseListItem = (props) => {


    let getBody = (props) => {
        let body = props.body

        if(body.length > 55){
            return body = body.slice(0, 55)
        }else{
            return body
        }    
    }

  return (


    <div>
        <h2>{props.name} - {props.price}</h2>
        <p>{getBody(props)}</p>

    </div>
  )
}

export default CourseListItem
