import React, {useContext} from 'react'
import { Link } from 'react-router-dom'
// import { Link } from 'react-router-dom'
import {UserContext} from '../context/UserContext'

const CourseListItem = (props) => {
    let {userInfo} = useContext(UserContext)

    // get body string - if it's too long => slice it!
    let getBody = (props) => {
        let body = props.body

        if(body.length > 55){
            return body = body.slice(0, 55) + '...'
        }else{
            return body
        }    
    }

    // get course price- if it's 0,00 => it's free!
    let getPrice = (props) => {
        let price = props.price

        if(props.price === '0.00'){
            return price = 'FREE'
        }else{
            return price + '$'
        }
    }

    let checkCurrentPremiumPlan = (props, userInfo) => {
        if(userInfo.premium_plan === 'grandmaster'){
            return true
        }else if(userInfo.premium_plan === 'international_master'){
            if(props.premiumPlan === 'grandmaster'){
                return false
            }else{
                return true
            }
        }else if(userInfo.premium_plan === 'master'){
            if(props.premiumPlan === 'grandmaster' || props.premiumPlan === 'international_master'){
                return false
            }else{
                return true
            }
        }else if(userInfo.premium_plan === 'free'){
            if(props.premiumPlan === 'free'){
                return true
            }else{
                return false
            }
        }
    }

    
    let buttonMsg = (props, userInfo) => {
        if(userInfo.user_acc_actv === 'True'){
            if(checkCurrentPremiumPlan(props, userInfo)){
                return 'Check it out'
            }else{
                return 'Buy access'
            }          
        }else{
            return 'Register'
        }
    }

    let buttonNavigate = (props, userInfo) => {
        if(userInfo.user_acc_actv === 'True'){
            if(checkCurrentPremiumPlan(props, userInfo)){
                return `/course/${props.slug}`
            }else{
                return '/premium-plans'
            }          
        }else{
            return <p className="card-text">{getBody(props)}</p>
        }
    }


  return (

    <div>
        
            
            <div className="col-sm-6">
                <div className="card" style={{width: '18rem', height:'13rem',}}>
                    <div className="card-body">
                        <h5 className="card-title">{props.name} - {getPrice(props)}</h5>
                        <p className="card-text">{getBody(props)}</p>
                        <Link to={buttonNavigate(props, userInfo)} className="btn btn-primary">{buttonMsg(props, userInfo)}</Link>
                                           
                    </div>
                </div>
            </div> 
        

        


        
    </div>
  )
}

export default CourseListItem
