import React from 'react'
// import { Link } from 'react-router-dom'


const CourseListItem = (props) => {

    let getBody = (props) => {
        let body = props.body

        if(body.length > 35){
            body = body.slice(0, 35)
        }

        return body
    }

  return (


    <div>
        <h2>{props.name}</h2>
        <p>{getBody(props)}</p>

    </div>
  )
}

export default CourseListItem
