import React, {useState, useEffect} from 'react'
import {useNavigate} from 'react-router-dom'

const Filtering = () => {

    let navigate = useNavigate()
    let [filterLink, setFilterLink] = useState({
        free: true,
        master: true,  
    })


    let freeID = document.querySelector('#free')
    let masterID = document.querySelector('#master')


    let filterNavigate = (dictOfFilters) => {
        console.log(dictOfFilters)
        let link = 'f'
        for (const [key, value] of Object.entries(dictOfFilters)) {
            if(value){
                link = link + `;${key};`
            }


            console.log(link);
          }

        // navigate(`/${link}`)
    }


    let addFilter = (e) => {
        console.log(filterLink)

        const {value} = e.target
        if(e.target.checked){
            setFilterLink(filterLink => ({...filterLink, [value]: false}))
        }else{
            setFilterLink(filterLink => ({...filterLink, [value]: true}))
        }
        console.log(filterLink)

        filterNavigate(filterLink)
    }
    
    let funff = () => {
        let freeID = document.querySelector('#free')
        let masterID = document.querySelector('#master')

        if(freeID.checked){
            setFilterLink(filterLink => ({...filterLink, [freeID.value]: false}))
        }else{
            setFilterLink(filterLink => ({...filterLink, [freeID.value]: true}))

        }
    }
    
    useEffect(() => {
        funff()
    }, [])

    
  return (
    <div >
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="free" value="free" onChange={useEffect} defaultChecked={false} />
            <label className="form-check-label" htmlFor="inlineCheckbox1">Free</label>
        </div>
        <div className="form-check form-check-inline">
            <input className="form-check-input" type="checkbox" id="master" value="master" onChange={useEffect} defaultChecked={false}/>
            <label className="form-check-label" htmlFor="inlineCheckbox2">Master</label>
        </div>
        
    </div>
  )
}

export default Filtering
